+        } else {
+            echo "JSON Error: " . json_last_error_msg() . "\n";
+            echo "Raw Response (first 200 chars): " . substr($response, 0, 200) . "\n";
+        }
+    } else {
+        echo "No response received\n";
+    }
+    echo "---\n";
+    
+    return $response;
+}
