+// Function to read CSV header
+function read_csv_header($file_path) {
+    $file = fopen($file_path, "r");
+    $header = fgetcsv($file);
+    fclose($file);
+    return $header;
+}
+
+// Function to read CSV data
+function read_csv_data($file_path, $xField, $yField) {
+    $file = fopen($file_path, "r");
+    $pivotTable = [];
+    $firstRow = true;
+    while (($row = fgetcsv($file)) !== FALSE) {
+        if ($firstRow) {
+            $firstRow = false;
+            continue;
+        }
+        $xValue = $row[$xField];
