+    global $db;
+    $stmt = $db->prepare('UPDATE contacts SET name = :name, email = :email WHERE id = :id');
+    $stmt->execute([':id' => $id, ':name' => $name, ':email' => $email]);
+}
+
+// Function to delete a record
+function delete($id) {
+    global $db;
+    $stmt = $db->prepare('DELETE FROM contacts WHERE id = :id');
+    $stmt->execute([':id' => $id]);
